<?phpnamespace weapp\Seowhy\behavior;class ModuleInitBehavior {    protected static $actionName;    protected static $controllerName;    protected static $moduleName;    protected static $method;    // 行为扩展的执行入口必须是run    public function run(&$params){        self::$actionName = request()->action();        self::$controllerName = request()->controller();        self::$moduleName = request()->module();        self::$method = request()->method();    }}